path = os.listdir()

def make_directory(path):
    if not os.path.isdir(path):
        print ("pravim direktorij {0}".format(path))
        os.makedirs(path)
    else:
        return

def dirStructBuild(path):
    for name in path:
        if name.lower().endswith(('.tif')):
            #inputPdf = PdfFileReader(open(name, "rb"))
            #docInfo = inputPdf.getDocumentInfo()
            #print(docInfo.subject)
            mo = name_regex.findall(name)
            #print(name)
            #print(mo)
            #return mo[3], mo[4], mo[5]
            if len(mo)>2:
                mb=csv_opcina.query("OG_MB==@mo[3]")
                ver = csv_verzija.query("ID==@mo[4]")
            
                if mb['OG_MB'].any():

                    if not os.path.isdir("{0}/{1}/{2}".format(mb['OG_NAZIV'].to_string(index=False), 'PPGO', ver['IME'].to_string(index=False))):
                        make_directory("{0}/{1}/{2}".format(mb['OG_NAZIV'].to_string(index=False), 'PPGO', ver['IME'].to_string(index=False)))
                        
                    
                    if not os.path.isfile("{0}/{1}/{2}/{3}".format(mb['OG_NAZIV'].to_string(index=False), 'PPGO', ver['IME'].to_string(index=False),name)):
                        
                        shutil.copy2(name, "{0}/{1}/{2}".format(mb['OG_NAZIV'].to_string(index=False), 'PPGO', ver['IME'].to_string(index=False)))
                        print("kopiram file:{0}".format(name))
            else:                       
                print("file nije valjan: {0}".format(name))
        else:
            print("file nije valjan: {0}".format(name))

dirStructBuild(path)
